                          "required": false,
                          "description": "Количество возвращаемых каталогов (от `1` до `1000`).",
                          "schema": {
                              "type": "integer",
                              "format": "int32",
                              "default": 100,
                              "minimum": 1,
                              "maximum": 1000
                          }
